import { useState } from "react"
import classNames from 'classnames'
import ContentLoader from "react-content-loader"

function ContentBlock({ name, imageUrl, price, types, sizes }) {
    const typesName = ['тонкое', 'традиционное']
    const typesSize = [26, 30, 40]
    const [activTypes, setActiveTypes] = useState(types[0])
    const [activSize, setActiveSize] = useState(sizes[0])
    const onSelectType = (index) => {
        setActiveTypes(index)
    }
    const onSelectSize = (index) => {
        setActiveSize(index)
    }

return (
    <ContentLoader 
    speed={2}
    width={280}
    height={460}
    viewBox="0 0 280 460"
    backgroundColor="#f3f3f3"
    foregroundColor="#ecebeb"
    {...props}
  >
    <rect x="313" y="223" rx="3" ry="3" width="178" height="6" /> 
    <circle cx="140" cy="140" r="140" /> 
    <rect x="0" y="303" rx="0" ry="0" width="278" height="101" />
  </ContentLoader>
)

    return (
        <div className="pizza-block">
            <img
                className="pizza-block__image"
                src={imageUrl}
                alt="Pizza"
            />
            <h4 className="pizza-block__title">{name}</h4>
            <div className="pizza-block__selector">
                <ul>
                    {typesName.map((type, index) => (
                        <li
                            onClick={() => { onSelectType(index) }}
                            className={classNames({
                                active: activTypes === index,
                                disabled: !types.includes(index)
                            })}
                            key={type}>
                            {type}
                        </li>
                    ))}
                </ul>
                <ul>
                {typesSize.map((size, index) => (
                        <li
                            onClick={() => { onSelectSize(index) }}
                            className={classNames({
                                active: activSize === index,
                                disabled: !sizes.includes(size)
                            })}
                            key={size}>
                            {size} см.
                        </li>
                    ))}
                </ul>
            </div>
            <div className="pizza-block__bottom">
                <div className="pizza-block__price">от {price} ₽</div>
                <div className="button button--outline button--add">
                    <svg
                        width="12"
                        height="12"
                        viewBox="0 0 12 12"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg">
                        <path
                            d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                            fill="white"
                        />
                    </svg>
                    <span>Добавить</span>
                    <i>2</i>
                </div>
            </div>
        </div>

    )
}
export default ContentBlock
