const initialState = {
    items: [],
    isLoaded: false
}

const content = (state = initialState, action) => {
    if (action.type === 'SET_CONTENT') {
        return {
            ...state,
            items: action.payload,
            isLoaded: true
        }
    }
    if (action.type === 'SET_LOADED') {
        return {
            ...state,
            isLoaded: action.payload
        }
    }
    return state
}
export default content
