import { useCallback, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import Categories from "../components/Categories"
import ContentBlock from "../components/ContentBlock"
import LoadingBlock from "../components/loadingBlock/LoadingBlock"
import Sorting from "../components/Sorting"
import { fetchContent } from "../redux/actions/content"
import { setCategory } from "../redux/actions/filters"

const category = ['Мясо', 'ptic', 'vega', 'mega', 'shmega']
const sortItems = [
    { name: 'популярности', type: 'popular' },
    { name: 'цене', type: 'price' },
    { name: 'алфавиту', type: 'alphabet' }
]

function Home() {

    const dispatch = useDispatch()
    const items = useSelector(({ content }) => content.items)
    const isLoaded = useSelector(({ content }) => content.isLoaded)

    useEffect(() => {
        dispatch(fetchContent())
    }, [])

    const onSelectCategory = useCallback((index) => {
        dispatch(setCategory(index))
    }, [])

    return (
        <div className="container">
            <div className="content__top">
                <Categories onClickItems={onSelectCategory} items={category} />
                <Sorting items={sortItems} />
            </div>
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__items">
                {isLoaded ? items.map((obj) => <ContentBlock isLoading={true} key={obj.id} {...obj} />) : Array(10).fill(<LoadingBlock />)}

            </div>
        </div>
    )
}

export default Home
