export const fetchContent = (dispatch) => {
    axios.get('http://localhost:3001/content')
      .then(({ data }) => {
        dispatch(setContent(data))
      })
  
}

export const setContent = (items) => ({
    type: 'SET_CONTENT',
    payload: items,
})
